
//9. Write a program to print only even numbers till 50. 

package DevLabs_Assignments;

public class EvenNumbersTill50 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=1;i<=50;i++)
		{
			if(i%2==0)
			{
				System.out.println(i);
			}
		}

	}

}
